package com.Loops;

public class NestedLoopsExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int a=2;a<4;a++) {  //outer loop
	for(int b=3;b<5;b++) {  //inner loop
		System.out.println("selenium"+b);
		System.out.println();
		
	}
}
for (int a=3;a<8;a++) {
for (int b=2;b<=6;b++) {	
System.out.println("webdriver"+a+b);
System.out.println();
}
}
for(int a=6;a>-6;a=a-2) {
	for (int b=-2;b>-15;b=b-3) {
System.out.println("Nested For Loop"+a+b);
System.out.println();
	}}
/*for (int a=6;a>-6;a=-2) {
	for(int b=-2;b>-15;b=b-3) {
System.out.println("ForLoop"+a+b);
System.out.println();
}}*/

for(int index1=5;index1>=1;index1--)
{
for(int index2=1;index2<index1;index2++)
{
System.out.print(index2+" sai ");      
}
System.out.println();
}
for(int a=2;a<=8;a=a+2) {
	for(int b=a;b>=-4;b=b-2) {
	System.out.println(b+"Madhu");	
		
	}
}

	}}
	









