package com.Loops;

public class WhileLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int var=2;
while(var<=5) {
	System.out.println(var+"my life line");
	var++;
	System.out.println();
}
int a=-0;
while(a>=-5) {
	System.out.println(a+"sai");
	a--;
	System.out.println();
}
int b=1;
while(b<=5) {
	System.out.println(b+"whlile");
	b++;
	System.out.println();
	System.out.println("the current value of the variable is :-"+b);
}
	while (b<=12) {
		System.out.println("selenium"+b);
		b=b+5;
	}
	
	}
}
	


